<?php 

session_start();
include("connection.php");
include("functions.php");

if ($_SERVER['REQUEST_METHOD'] == "POST") {
    // Something was posted
    $user_name = $_POST['user_name'];
    $password = $_POST['password'];

    // Static admin credentials
    $admin_username = "admin"; // Change this to your desired admin username
    $admin_password = "admin_password"; // Change this to your desired admin password

    if (!empty($user_name) && !empty($password) && !is_numeric($user_name)) {
        // Check for admin credentials
        if ($user_name === $admin_username && $password === $admin_password) {
            $_SESSION['user_id'] = "admin"; // Set a session variable for admin
            header("Location: admin.php"); // Redirect to admin panel
            die;
        }

        // Read from database for regular users
        $query = "SELECT * FROM users WHERE user_name = '$user_name' LIMIT 1";
        $result = mysqli_query($con, $query);

        if ($result) {
            if ($result && mysqli_num_rows($result) > 0) {
                $user_data = mysqli_fetch_assoc($result);
                
                if ($user_data['password'] === $password) {
                    $_SESSION['user_id'] = $user_data['user_id'];
                    header("Location: index.php");
                    die;
                }
            }
        }
        
        echo "wrong username or password!";
    } else {
        echo "wrong username or password!";
    }
}
